import React from 'react';
import { useSession, CombinedDataProvider, Text } from "@inrupt/solid-ui-react";
import { FOAF, VCARD } from "@inrupt/vocab-common-rdf";


function Header(props) {

  const { session } = useSession();
  const webId = session.info.webId;

  return(
    <>
      <h2>&nbsp;&nbsp;&nbsp;&nbsp;Welcome to my-solid-blog</h2>
      <div className="columns">
        <div className="column notification is-warning is-two-fifths">
          <p>&nbsp;WebID:&nbsp;&nbsp;<a href={webId} target="_blank" rel="noreferrer">{webId}</a></p>
          <CombinedDataProvider
            datasetUrl={session.info.webId}
            thingUrl={session.info.webId}
          >
            <h4>&nbsp;Name:&nbsp;
              <Text properties={[
                VCARD.fn,
                FOAF.name,
              ]} />
            &nbsp;&nbsp;&nbsp;&nbsp;Organization:&nbsp;
              <Text properties={[
                VCARD.organization_name
              ]} />
            &nbsp;&nbsp;&nbsp;&nbsp;Role:&nbsp;
              <Text properties={[
                VCARD.role
              ]} />
            </h4>
          </CombinedDataProvider>
        </div>
        <div className="column notification is-warning is-one-third">
          <p>prova</p>
        </div>
      </div>
    </>
  );
}

export default Header;
