import {
  createSolidDataset,
  getSolidDataset,
  saveSolidDatasetAt,
} from "@inrupt/solid-client";

export async function getOrCreateDataset(containerUri, fetch, publicTypeIndexUri) {

  const notelistUri = containerUri + "articlelist.ttl";

  const publicTypeIndex = getSolidDataset(publicTypeIndexUri);

  try {
    const notelistDataset = await getSolidDataset(
      notelistUri,
      {
      fetch: fetch,
      },
    );
    return notelistDataset;
  } catch(e) {
    if(e.statusCode === 404) {
      const notelistDataset = await saveSolidDatasetAt(
        notelistUri,
        createSolidDataset(),
        {
        fetch: fetch,
        },
      );
      return notelistDataset
    }
  }
  return;
}
