import React, { useState } from "react";
import { 
  getStringNoLocale,
  getDatetime,
  getSourceUrl
} from "@inrupt/solid-client";
import {  CombinedDataProvider, Text } from "@inrupt/solid-ui-react";
import { schema } from 'rdf-namespaces';
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

function Article(props){

  const articleDate = props.thing ? (
    getDatetime(
      props.thing,
      schema.dateCreated,
    )
  ) : ("");

  console.log("ECCO LA DATA " + articleDate);

  const titleText = props.thing ? (
    getStringNoLocale(
      props.thing,
      schema.headline,
    )
  ) : ("");

  const contentText = props.thing ? (
    getStringNoLocale(
      props.thing,
      schema.text,
    )
  ) : ("");

  const [isEditing, setEditing] = useState(false);
  const [title, setTitle] = useState(titleText);
  const [content, setContent] = useState(contentText);
  //const [date, setDate] = useState(dateText);

  const removeArticle = async () => {
    await props.remove(props.thing);
  };

  const saveArticle = async () => {
    await props.change(props.thing, content, title);
    setTitle(titleText);
    setContent(contentText);
    setEditing(false);
  };

  const cancelChange = () => {
    setTitle(title);
    setContent(content);
    setEditing(false);
  }

  const setEditingTrue = () => {
    setEditing(true);
  }

  const handleChangeTitle = (event) => {
    event.preventDefault();
    setTitle(event.target.value);
  }

  const handleChangeContent = (event) => {
    event.preventDefault();
    setContent(event.target.value);
  }

  const handleSubmit = async (event) => {
    event.preventDefault();
    await saveArticle();
  };

  return(
    <>
      { !isEditing ? (
        <div>
          <br/>
          <div className="columns">
            <div className="column  is-three-quarters">
              <div className="notification is-warning is-light has-text-weight-bold">
                {title}
              </div>
            </div>
            <div className="column  is-one-fifth">
              <div className="notification is-danger is-light">
              <CombinedDataProvider
                datasetUrl={props.source}
                thingUrl={props.thing}
              >
                  <Text properties={[
                    schema.dateCreated,
                  ]} />
              </CombinedDataProvider>   
              </div>
            </div>
          </div>
          <div className="card">
            <div className="card-content">
              <div className="content">
                {content}
              </div>
            </div>
          </div>
          <br/>
          <button className="button" onClick={() => setEditingTrue()}>
            <span className="icon is-small">
              <FontAwesomeIcon icon="pencil-alt"/>
            </span>
          </button>
          <span>&nbsp;&nbsp;</span>
          <button className="button" onClick={async () => await removeArticle()}>
            <span className="icon is-small">
              <FontAwesomeIcon icon="times"/>
            </span>
          </button>
          <br/>
        </div>
      ) : (
        <div>
          <br/>
          <form onSubmit={handleSubmit}>
            <textarea 
              className="textarea is-danger"
              onChange={handleChangeTitle}
              value={title}
              rows="2"
            >
            </textarea>
            <textarea 
              className="textarea is-warning"
              onChange={handleChangeContent}
              value={content}
              rows="5"
            >
            </textarea>
            <br/>
            <button className="button is-danger" onClick={cancelChange}>Cancel</button>
            &nbsp;
            <button type="submit" className="button is-warning">Save</button>
            &nbsp;
          </form>    
          <br/>
        </div>
      )}
    </>
  );   
}

export default Article;
