import { 
    saveSolidDatasetAt,
    getSolidDataset,
    getSourceUrl,
    getBoolean,
    getDate,
    getDecimal,
    getInteger,
    getStringWithLocale,
    getUrl,
    getUrlAll,
    getTime,
    removeThing,
    getThing,
    getThingAll,
    setThing,
    createThing,
    addDatetime,
    addStringNoLocale,
    setStringNoLocale,
    getStringNoLocale,
    getDatetime,
  } from "@inrupt/solid-client";
  import { useSession } from "@inrupt/solid-ui-react";
  import React, { useEffect, useState } from "react";
  import { getOrCreateDataset } from "../utils/GetOrCreateDataset.js";
  import WriteArticles from './WriteArticles.js';
  import Article from './Article.js';
  import { schema, rdf, acl, solid } from 'rdf-namespaces';
  //eliminate later
  import { FOAF, VCARD } from "@inrupt/vocab-common-rdf";


  function Articlelist() {
    const { session } = useSession();
    const [articlelist, setArticlelist] = useState();
    //eliminate later
    const [eliminare, setEliminare] = useState();
    const iriProperty = "http://www.w3.org/ns/auth/acl#trustedApp";

    useEffect(() => {
      let isComponentMounted = true;

      const fetchDataset = async () => {
        try {
          const myDataset = await getSolidDataset(
            session.info.webId,
            {
            fetch: session.fetch,
            },
          );
          const profile = getThing(
            myDataset,
            session.info.webId,
          );
          //eliminare dopo
          const name = getStringNoLocale( profile, VCARD.fn )

          const publicIndexUrl = getUrl( profile, solid.publicTypeIndex );
          const publicIndex = await getSolidDataset( publicIndexUrl );
          console.log( "Ecco l'url del tipo pubblico: " + publicIndexUrl );

          const attemptOne = getStringNoLocale( profile, iriProperty + "[" + acl.mode + "]");
          const attemptTwo = getBoolean( profile, iriProperty + "[" + acl.mode + "]" );
          const attemptThree = getDate( profile, iriProperty + "[" + acl.mode + "]" );
          const attemptFour = getDatetime( profile, iriProperty + "[" + acl.mode + "]" );
          const attemptFive = getDecimal( profile, iriProperty + "[" + acl.mode + "]" );
          const attemptSix = getInteger( profile, iriProperty + "[" + acl.mode + "]" );
          const attemptSeven = getTime( profile, iriProperty + "[" + acl.mode + "]" );
          const attemptEight = getUrl( profile, iriProperty + "[" + acl.mode + "]" );

          console.log("prova prova: " + attemptOne + " " + attemptTwo + " " + attemptThree + " " + attemptFour + " " + attemptFive + " " + attemptSix + " " + attemptSeven + " " + attemptEight);
          console.log("ecco il nome: " + name);
          setEliminare(name);


          //fino a qui

          const podUrl = getUrl(
            profile,
            "http://www.w3.org/ns/pim/space#storage",
          );
          const containerUrl = podUrl + "my-solid-blog/";
          const list = await getOrCreateDataset(
            containerUrl,
            session.fetch,
          );
          if (isComponentMounted) {
            setArticlelist(list);
          }
        }
        catch(e) {
          console.log("an error occurr");
          console.log(e);
        }
      };

      fetchDataset();

      return () => {
        isComponentMounted = false;
      }
    }, [session]);


    const addArticle = async (articleTitle, articleText) => {
      const articlelistUrl = getSourceUrl(articlelist);

      const thingWithType = addStringNoLocale(
        createThing(),
        rdf.type,
        schema.TextDigitalDocument,
      );
      const thingWithTitle = addStringNoLocale(
        thingWithType,
        schema.headline,
        articleTitle,
      );
      const thingWithDate = addDatetime(
        thingWithTitle,
        schema.dateCreated,
        new Date(),
      );
      const thingWithText = addStringNoLocale(
        thingWithDate,
        schema.text,
        articleText,
      );
      const updatedArticlelist = setThing(
        articlelist,
        thingWithText,
      );

      const savedUpdatedArticlelist = await saveSolidDatasetAt(
        articlelistUrl,
        updatedArticlelist,
        {fetch: session.fetch},
      );
      setArticlelist(savedUpdatedArticlelist);
    };

    const removeArticle = async (thing) => {
      const articlelistUrl = getSourceUrl(articlelist);
      const updatedArticlelist = removeThing(
        articlelist,
        thing,
      );
      const savedUpdatedArticlelist = await saveSolidDatasetAt(
        articlelistUrl,
        updatedArticlelist,
        {fetch: session.fetch},
      );
      setArticlelist(savedUpdatedArticlelist);
    }

    const changeArticle = async (thing, text, headline) => {
      const articlelistUrl = getSourceUrl(articlelist);
      const articleText = setStringNoLocale(
        thing,
        schema.text,
        text,
      );
      const articleHeadline = setStringNoLocale(
        articleText,
        schema.headline,
        headline,
      );
      const updatedArticlelist = setThing(
        articlelist,
        articleHeadline,
      );
      const savedUpdatedArticlelist = await saveSolidDatasetAt(
        articlelistUrl,
        updatedArticlelist,
        {fetch: session.fetch},
      );
      setArticlelist(savedUpdatedArticlelist);
    };

    const articlesArray = articlelist ? getThingAll(articlelist) : [];  
    const myArticles = articlesArray.sort(byDate).map((currentValue) => {
      const title = getStringNoLocale(
        currentValue,
        schema.headline,
      );

      return(
        <li key={title} className="columns">
          <Article
            thing={currentValue}
            remove={async (thing) => await removeArticle(thing)}
            change={async (thing, text, headline) => await changeArticle(thing, text, headline)}
          />
        </li>
      );
    });

    return(
      <>
        <br/>
        {/*eliminare div successivo*/}
        <div>
          {eliminare}
        </div>
        <div className="columns">
          &nbsp;&nbsp;&nbsp;&nbsp;
          <div className="column is-half">
          <ul>{myArticles}</ul>
          </div>
          <div className="column is-one-quarter">
            <div className="card">
              <div className="card-content">
                <div className="content">
                  You can use TrustApp in order to undestand if the permissions are still valid.
                </div>
                <br/>
                <div className="content">
                  This application is a part of a project.
                </div>
                <br/>
              </div>
            </div>
          </div>
        </div>
        <br/>
        <br/>
        <WriteArticles
          newArticle={(title, text) => addArticle(title, text)}
        />
      </>
    );
  }

  export default Articlelist;


  function byDate(a, b) {
    const aUpdateDate = getDatetime(a, schema.dateModified);
    const bUpdateDate = getDatetime(b, schema.dateModified);

    const aCreatedDate = getDatetime(a, schema.dateCreated);
    const bCreatedDate = getDatetime(b, schema.dateCreated);

    const aDate = aUpdateDate ?? aCreatedDate;
    const bDate = bUpdateDate ?? bCreatedDate;

    if (bDate === null) {
      return -1;
    }

    if (aDate === null) {
      return 1;
    }

    return bDate.getTime() - aDate.getTime();
  }
