import React from 'react';
import { useSession, CombinedDataProvider, Text } from "@inrupt/solid-ui-react";
import { FOAF, VCARD } from "@inrupt/vocab-common-rdf";
import axios from 'axios';


function Header(props) {

  const { session } = useSession();
  const webId = session.info.webId;
  const serverUrl = "";

  const handleSubmitRead = (event) => {
    event.preventDefault();

    axios({
      method: 'post',
      url: serverUrl,
      data: {
        webId: session.info.webId,
        permission: 'read',
      },
    })
    .then((response) => {

    });
  };

  const handleSubmitAppend = (event) => {
    event.preventDefault();

    axios({
      method: 'post',
      url: serverUrl,
      data: {
        webId: session.info.webId,
        permission: 'append',
      },
    })
    .then((response) => {

    });
  };

  const handleSubmitWrite = (event) => {
    event.preventDefault();

    axios({
      method: 'post',
      url: serverUrl,
      data: {
        webId: session.info.webId,
        permission: 'write',
      },
    })
    .then((response) => {

    });
  };

  return(
    <>
      <h2>&nbsp;&nbsp;&nbsp;&nbsp;Welcome to my-solid-blog</h2>
      <div className="columns">
        <div className="column is-two-fifths">
          <div className="card">
            <div className="card-content">
              <p>&nbsp;WebID:&nbsp;&nbsp;<a href={webId} target="_blank" rel="noreferrer">{webId}</a></p>
              <CombinedDataProvider
                datasetUrl={session.info.webId}
                thingUrl={session.info.webId}
              >
                <div className="notification is-warning is-light">
                <div className="content has-text-weight-bold">&nbsp;Name:&nbsp;
                  <Text properties={[
                    VCARD.fn,
                    FOAF.name,
                  ]} />
                &nbsp;&nbsp;&nbsp;&nbsp;Organization:&nbsp;
                  <Text properties={[
                    VCARD.organization_name
                  ]} />
                &nbsp;&nbsp;&nbsp;&nbsp;Role:&nbsp;
                  <Text properties={[
                    VCARD.role
                  ]} />
                </div>
                </div>
              </CombinedDataProvider>
            </div>
          </div>
        </div>
        <div className="column is-one-third">
          <div className="card">
            <div className="card-content">
              <h4>Use TrustApp to check the permissions</h4>&nbsp;&nbsp;
              <div className="columns">
                <div className="column is-narrow">
                  <form onSubmit={handleSubmitRead}>
                    <button type="submit" className="button is-danger is-light">Read</button>
                  </form>
                </div>
                <div className="column is-one-fifth">
                  <form onSubmit={handleSubmitAppend}>
                    <button type="submit" className="button is-danger is-light">Append</button>
                  </form>
                </div>
                <div className="column is-one-fifth">
                  <form onSubmit={handleSubmitWrite}>
                    <button type="submit" className="button is-danger is-light">Write</button>
                  </form>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
      <h3>&nbsp;&nbsp;Read my articles here</h3>
    </>
  );
}

export default Header;
