import React from 'react';
import { LoginButton } from "@inrupt/solid-ui-react";

const SOLID_IDENTITY_PROVIDER = "https://inrupt.net/";
const authOptions = {
    clientName: "my-solid-blog",
};


class LoginArea extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      solidIdentityProvider: SOLID_IDENTITY_PROVIDER,
      value: '',      
    };

    this.handleChange = this.handleChange.bind(this);
  }

  handleChange(event) {
    this.setState({value: event.target.value});
  }

  changeProvider() {
    const solidIdentityProvider = this.state.value;
    this.setState({solidIdentityProvider: solidIdentityProvider});
  }

  render() {
    return (
      <div>
        <p className="title">Welcome to my-solid-blog</p>
        <p className="subtitle">Please, connect to your pod to start</p>
        <p>Change the address of your Solid Identity Provider</p>
        <div className="columns">
          <div className="column">
            <div className="control">
              <input className="input is-danger is-small" list="sol-id-prov" value={this.state.value} onChange={this.handleChange}/>
              <datalist id="sol-id-prov">
                <option value="https://solidcommunity.net" />
                <option value="https://inrupt.net/" />
              </datalist>
            </div>
          </div>
          <div className="column">
            <div className="control">
              <button className="button is-warning" onClick={() => this.changeProvider()}>Save</button>
            </div>
          </div>
        </div>
        <p>You are trying to connect into <a href={this.state.solidIdentityProvider} target="_blank" rel="noreferrer">{this.state.solidIdentityProvider}</a></p>
        <LoginButton
        oidcIssuer={this.state.solidIdentityProvider}
        redirectUrl={window.location.href}
        authOptions={authOptions}
        >
          <button className="button is-warning is-medium">
            Log&nbsp;in
          </button>
        </LoginButton>
        <p>You can also uso </p>

      </div>
    );
  }

}

export default LoginArea;
