import React, { useState } from 'react';
import { LoginButton } from "@inrupt/solid-ui-react";


function LoginArea(props) {

  const webId = "https://carlotosoni99.inrupt.net/profile/card#me";

  const SOLID_IDENTITY_PROVIDER_DEFAULT = "https://inrupt.net/";
  const authOptions = {
      clientName: "my-solid-blog",
  };

  const [inputContent, setInputContent] = useState("");
  const [solidIdentityProvider, setSolidIdentityProvider] = useState(SOLID_IDENTITY_PROVIDER_DEFAULT);


  const handleChange = (event) => {
    event.preventDefault();
    setInputContent(event.target.value);
  }

  const changeProvider = () => {
    setSolidIdentityProvider(inputContent);
  }

  return (
    <div>
      <p className="title">Welcome to my-solid-blog</p>
      <p className="subtitle">Please, connect to your pod to start</p>
      <p>Change the address of your Solid Identity Provider</p>
      <div className="columns">
        <div className="column">
          <div className="control">
            <input className="input is-danger is-small" list="sol-id-prov" value={inputContent} onChange={handleChange}/>
            <datalist id="sol-id-prov">
              <option value="https://solidcommunity.net" />
              <option value="https://inrupt.net/" />
            </datalist>
          </div>
        </div>
        <div className="column">
          <div className="control">
            <button className="button is-warning" onClick={() => changeProvider()}>Save</button>
          </div>
        </div>
      </div>
      <p>You are trying to connect into <a href={solidIdentityProvider} target="_blank" rel="noreferrer">{solidIdentityProvider}</a></p>
      <LoginButton
        oidcIssuer={solidIdentityProvider}
        redirectUrl={window.location.href}
        authOptions={authOptions}
      >
        <button className="button is-warning is-medium">
          Log&nbsp;in
        </button>
      </LoginButton>
      <br/>
      <p>You can also use the application without Logging in (in this case you will read the articles of <a href={webId} target="_blank" rel="noreferrer">{solidIdentityProvider}</a></p>

      <button className="button is-warning"
        onClick={() => props.entering()}
      >
        Enter
      </button>

    </div>
  );


}

export default LoginArea;
