import React, { useState } from "react";
import { 
  getStringNoLocale,
  getDate,
} from "@inrupt/solid-client";
import { schema } from 'rdf-namespaces';
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

function Article(props){

  const dateText = props.thing ? (
    getDate(
      props.thing,
      schema.dateCreated,
    )
  ) : ("");

  const titleText = props.thing ? (
    getStringNoLocale(
      props.thing,
      schema.headline,
    )
  ) : ("");

  const contentText = props.thing ? (
    getStringNoLocale(
      props.thing,
      schema.text,
    )
  ) : ("");

  const [isEditing, setEditing] = useState(false);
  const [title, setTitle] = useState(titleText);
  const [content, setContent] = useState(contentText);
  const [date, setDate] = useState(dateText);

  const removeArticle = async () => {
    await props.remove(props.thing);
  };

  const saveArticle = async () => {
    await props.change(props.thing, content, title);
    setTitle(titleText);
    setContent(contentText);
    setEditing(false);
  };

  const cancelChange = () => {
    setTitle(title);
    setContent(content);
    setEditing(false);
  }

  const setEditingTrue = () => {
    setEditing(true);
  }

  const handleChangeTitle = (event) => {
    event.preventDefault();
    setTitle(event.target.value);
  }

  const handleChangeContent = (event) => {
    event.preventDefault();
    setContent(event.target.value);
  }

  const handleSubmit = async (event) => {
    event.preventDefault();
    await saveArticle();
  };

  return(
    <>
      { !isEditing ? (
        <div>
          <br/>
          <div className="columns">
            <div className="column  is-three-quarters">
              <div className="notification is-warning is-light has-text-weight-bold">
                {title}
              </div>
            </div>
            <div className="column  is-one-fifth">
              <div className="notification is-danger is-light">
                {date}
              </div>
            </div>
          </div>
          <div className="card">
            <div className="card-content">
              <div className="content">
                {content}
              </div>
            </div>
          </div>
          <button className="button" onClick={() => setEditingTrue()}>
            <span className="icon is-small">
              <FontAwesomeIcon icon="pencil-alt"/>
            </span>
          </button>
          <span>&nbsp;&nbsp;</span>
          <button className="button" onClick={async () => await removeArticle()}>
            <span className="icon is-small">
              <FontAwesomeIcon icon="times"/>
            </span>
          </button>
          <br/>
        </div>
      ) : (
        <div>
          <br/>
          <p>Ancora nulla</p>
          <br/>
        </div>
      )}
    </>
  );   
}

export default Article;
